package com.leliu.ycyocp.consumer;

import com.leliu.ycyocp.model.ConsumeResult;
import com.leliu.ycyocp.model.Product;
import com.leliu.ycyocp.producer.YcyProducer;

/**
 * The consumer interface, which will be used to consume the {@link Product}
 * which is expected to be generated by {@link YcyProducer}.
 *
 * @param <TProduct>
 * @param <TConsumeResult>
 */
public interface YcyConsumer<TProduct extends Product, TConsumeResult extends ConsumeResult> {
    /**
     * Consume the product then return the consuming result.
     *
     * @param product
     * @return
     */
    TConsumeResult consume(TProduct product);
}
