package com.leliu.ycyocp.model;

import com.leliu.ycyocp.producer.YcyProducer;

/**
 * The abstract Product. All the product generated by the {@link YcyProducer} should extend this model.
 */
public abstract class Product {
    /**
     * The string format that represents the product.
     *
     * @return
     */
    public abstract String stringify();

    @Override
    public String toString() {
        return this.stringify();
    }
}
